import React, { useEffect, useRef, useState } from 'react';
import './App.css';
import AppleLogo from './assets/applePixels.png';
import Monitor from './assets/black.jpeg';
import answer0 from './assets/answer0.png';
import answer1 from './assets/answer1.png';
import answer2 from './assets/answer2.png';
import answer3 from './assets/answer3.png';
import answer4 from './assets/answer4.png';
import useInterval from './hooks/useInterval';
import logo from './assets/logo.svg'
import { create } from 'domain';


const canvasX = 350;
const canvasY = 480;
const initialSnake = [
  [4, 5],
  [4, 6],
];
const initialApple = [14, 10];
const scale = 50;
const timeDelay = 100;
const questions = ["Hur glad har du känt dig de senaste 7 dagarna?", 
"Hur ofta har du känt dig motiverad i skolan de senaste 7 dagarna?", 
"Jag upplever att mina lärare har varit stödjande de senaste 7 dagarna.",
"Jag upplever att mina klasskamrater har varit stödjande de senaste 7 dagarna", 
"Jag är nöjd med min insats i skolan de senaste 7 dagarna.", 
"Jag har upplevt en hög arbetsbelastning och stress de senaste 7 dagarna.", "Hur ofta har du mått dåligt de senaste 7 dagarna?", 
"Hur ofta har du upplevt oro de senaste 7 dagarna?"];

const answers = [answer0, answer1, answer2, answer3, answer4];


const rectWidth = 120
const rectHeight = 50

const answerPos = [[canvasX / 2 - rectWidth / 2, 20],
                   [canvasX / 2 - rectWidth / 2, 2*20 + rectHeight],
                   [canvasX / 2 - rectWidth / 2, 3*20 + rectHeight],
                   [canvasX / 2 - rectWidth / 2, 4*20 + rectHeight],
                   [canvasX / 2 - rectWidth / 2, 5*20 + rectHeight]];


const App = () => {
  const canvasRef = useRef<HTMLCanvasElement | null>(null);
  const [snake, setSnake] = useState(initialSnake);
  const [apple, setApple] = useState(initialApple);
  const [direction, setDirection] = useState([0, -1]);
  const [delay, setDelay] = useState<number | null>(null);
  const [score, setScore] = useState(0);
  const [currentQuestion, setCurrentQuestion] = useState(questions[0]);

  useInterval(() => runGame(), delay);

  useEffect(() => {
    let fruit = document.getElementById('fruit') as HTMLCanvasElement;
    if (canvasRef.current) {
      const canvas = canvasRef.current;
      const ctx = canvas.getContext('2d');
      if (ctx) {
        // ctx.setTransform(scale, 0, 0, scale, 0, 0);
        // ctx.clearRect(0, 0, window.innerWidth, window.innerHeight);
        // ctx.fillStyle = '#a3d001';
        // snake.forEach(([x, y]) => ctx.fillRect(x, y, 1, 1));
        // ctx.drawImage(fruit, apple[0], apple[1], 1, 1);
      }
    }
  }, [snake, apple]);

  function handleSetScore() {
    if (score > Number(localStorage.getItem('snakeScore'))) {
      localStorage.setItem('snakeScore', JSON.stringify(score));
    }
  }

  function setDPI() {
    // Set up CSS size.
    let dpi = 300;
    if (canvasRef.current) {
      const canvas = canvasRef.current;
      const ctx = canvas.getContext('2d');
      if (ctx) {
        canvas.style.width = canvas.style.width || canvas.width + 'px';
        canvas.style.height = canvas.style.height || canvas.height + 'px';

        // Get size information.
        var scaleFactor = dpi / 96;
        var width = parseFloat(canvas.style.width);
        var height = parseFloat(canvas.style.height);

        // Backup the canvas contents.
        var oldScale = canvas.width / width;
        var backupScale = scaleFactor / oldScale;
        // var backup = canvas.cloneNode(false);
        // backup.getContext('2d').drawImage(canvas, 0, 0);

        // Resize the canvas.
        var ctXX = canvas.getContext('2d');
        canvas.width = Math.ceil(width * scaleFactor);
        canvas.height = Math.ceil(height * scaleFactor);

        // Redraw the canvas image and scale future draws.
        ctx.setTransform(backupScale, 0, 0, backupScale, 0, 0);
        // ctx.drawImage(backup, 0, 0);
        ctx.setTransform(scaleFactor, 0, 0, scaleFactor, 0, 0);
      }
    }
  }

  function setQuestion(number: number) {
    //localStorage.setItem('question', questions[number]);
    setCurrentQuestion(questions[number]);
  }

  function createAnswers() {
    if (canvasRef.current) {
      const canvas = canvasRef.current;
      const ctx = canvas.getContext('2d');
      if (ctx) {
        let y = 10;
        for (let i = 0; i < 5; i++) {
          let answer = document.getElementById('answer'+i) as HTMLCanvasElement;
          ctx.drawImage(answer,  answerPos[i][0], answerPos[i][1], rectWidth, rectHeight); 
          y += rectHeight + 10;  
        }
      }
    }
  }

  function createBall(){
    if (canvasRef.current) {
      const canvas = canvasRef.current;
      const ctx = canvas.getContext('2d');
      if (ctx) {
        ctx.beginPath();
        ctx.fillStyle = '#00FF00';
        ctx.arc(canvasX / 2, canvasY - 20, 10, 0, 2 * Math.PI);
        ctx.closePath();
        ctx.fill();
      }
    }
  }

  function play() {
    // setSnake(initialSnake);
    // setApple(initialApple);
    // setDirection([1, 0]);
    setDPI();
    setDelay(timeDelay);
    setCurrentQuestion(questions[0]);
    // setScore(0);
    createAnswers();
    createBall();
  }
  function checkCollision(head: number[]) {
    // for (let i = 0; i < snake.length; i++) {
    //   if (head[i] < 0 || head[i] * scale >= canvasX) {
    //   }
    //   return true;
    // }
    // // for (const s of snake) {
    //   if (head[0] === s[0] && head[1] === s[1]) return true;
    // }
    return false;
  }
  function appleAte(newSnake: number[][]) {
    let coord = apple.map(() => Math.floor((Math.random() * canvasX) / scale));
    if (newSnake[0][0] === apple[0] && newSnake[0][1] === apple[1]) {
      let newApple = coord;
      setScore(score + 1);
      setApple(newApple);
      return true;
    }
    return false;
  }

  function runGame() {
    console.log('RUNNING GAME');
    // const newSnake = [...snake];
    // const newSnakeHead = [
    //   newSnake[0][0] + direction[0],
    //   newSnake[0][1] + direction[1],
    // ];
    // newSnake.unshift(newSnakeHead);
    // if (checkCollision(newSnakeHead)) {
    //   setDelay(null);
    //   handleSetScore();
    // }
    // if (!appleAte(newSnake)) {
    //   newSnake.pop();
    // }
    // setSnake(newSnake);
    createAnswers();
  }
  function changeDirection(e: React.KeyboardEvent<HTMLDivElement>) {
    switch (e.key) {
      case 'ArrowLeft':
        setDirection([-1, 0]);
        break;
      case 'ArrowUp':
        setDirection([0, -1]);
        break;
      case 'ArrowRight':
        setDirection([1, 0]);
        break;
      case 'ArrowDown':
        setDirection([0, 1]);
        break;
    }
  }

  return (
    <div onKeyDown={e => changeDirection(e)}>
      <h2>{currentQuestion}</h2>
      <img id="fruit" src={AppleLogo} alt="fruit" width="30" />
      <img id="answer0" src={answer0} alt="answer" width="30" />
      <img id="answer1" src={answer1} alt="answer" width="30" />
      <img id="answer2" src={answer2} alt="answer" width="30" />
      <img id="answer3" src={answer3} alt="answer" width="30" />
      <img id="answer4" src={answer4} alt="answer" width="30" />
      <canvas
        className="playArea"
        ref={canvasRef}
        width={`${canvasX}px`}
        height={`${canvasY}px`}
      />
      <button onClick={play} className="playButton">
        Play
      </button>
    </div>
  );
};

export default App;
